import React, { useEffect, useState } from "react"
import contentEditable from "react-contenteditable"

function Note() {
    const [ header, setHeader ] = useState("Header")
    const [ textBody, setTextBody ] = useState("Text body")

    useEffect(() => {
        console.log(header)
    }, [header])

    return (
        // <span>
