        //     />
        //     <input 
        //             className="note-text"
        //             value={textBody}
        //             onChange={e => setTextBody(e.target.value)}
        //         />
        // </span>
        <div>
            <span onChange={e => setHeader(e.target.value)} className="note-header" contentEditable="true">{header}</span>
            <span onChange={e => setTextBody(e.target.value)}  className="note-text" contentEditable="true">{textBody}</span>
            <contentEditable  
                html={textBody}
                className="content-editable"
                onChange={e => setTextBody(e.target.value)}  
            />
        </div>
    )
}

export default Note
