import React, { useEffect, useState } from "react"
import IndexItem from "./components/IndexItem"
import ContentEditable from "react-contenteditable"
import { NoteDB } from "./data/NoteDB"
import './App.css'

function App() {
  const [ notes, setNotes ] = useState([])
  const [ error, setError ] = useState(false)
  const [ header, setHeader ] = useState("Header")
  const [ text, setText ] = useState("Text body")

  // Opens object as note
  function OpenNote(e) {
    // Get ID
    console.log(e.target.id)
    const selectID = e.target.id
    // Load ID object to note page
    let selectedNote
    for(const item of NoteDB) {
      if(item.index == selectID) {
        selectedNote = item
      }
    }
    console.log(selectedNote)
    setHeader(() => selectedNote.header)
    setText(() => selectedNote.text)
  }

  // Removes styling from ctrl+c pasting
  function PasteAsPlainText (event) {
    event.preventDefault()

    const text = event.clipboardData.getData("text/plain")
    document.execCommand("insertHTML", false, text)
  }

  const printNotes = NoteDB.map(item => <IndexItem key={item.index} index={item.index} header={item.header} text={item.text} OpenNote={OpenNote} />)

  return (
    <div className="app-container">

      <div className="navbar">
        <div className="navbar-logo">
          <p>SimpleNote</p>
        </div>
      </div>

      <div className="note-container">
        <div className="note-index">
          {printNotes}
        </div>

        <div className="note-page">
          <ContentEditable  
            html={header}
            className="content-editable note-header"
            onChange={e => setHeader(e.target.value)}  
            onPaste={PasteAsPlainText}
          />
          <ContentEditable  
            html={text}
            className="content-editable note-text"
            onChange={e => setText(e.target.value)}  
            onPaste={PasteAsPlainText}
          />
        </div>
      </div>
    </div>
  )
}

export default App
